type_definitions.definition:
interface MandatoryObject {
    key: string;
    type: "string" | "number" | "boolean" | "object" | "array";
    length?: number;
    required_keys?: string[];
    array_types?: ("string" | "number" | "boolean" | "object")[];
}

examples.code:
const emailRule: MandatoryObject = {
  key: "email",
  type: "string",
  length: 5
};

type_definitions.definition:
interface MandatoryParams {
    body?: MandatoryObject[];
    headers?: MandatoryObject[];
}

interface LogRequests {
    url?: boolean;
    headers?: boolean;
    query?: boolean;
    body?: boolean;
}

interface AppOptions {
    port?: number;
    log_requests?: LogRequests;
    init_snapshot_options?: InitSnapshotsOptions;
    initialize_redis?: boolean;
}
